import React, { useState,use } from 'react'
import { Card, CardBody } from 'react-bootstrap';

const Ordinary = () => {
    const[ordinary,setOrdinary]=useState([]);
    useEffect(()=>{
        fetch('https://www.thecocktaildb.com/api/json/v1/1/filter.php?c=Ordinary_Drink')
        .then((response)=> response.json())
        .then((data)=>{
          console.log(data.drinks);
          setOrdinary(data.drinks);
        })
        .catch((error)=>{
          console.log(error.message);
        })
      },[])
  return (
    <div>
       <div>
        <h1>Ordinary Drinks</h1>
        <div>
            {ordinary.map((drink)=>(
                 <Card
                 key={drink.idDrink}
                 style={{
                   width: "16rem",
                   margin: "20px",
                   backgroundColor: "rgb(187 187 187)",
                 }}
                 onClick={() => handleItemClick(drink)}
               >
                 <Card.Img
                   className="image"
                   src={drink.strDrinkThumb}
                   style={{ width: "200px", height: "200px" }}
                   alt={drink.strDrink}
                 />
                 <Card.Title
                   style={{ fontSize: "16px", fontFamily: "Times New Roman" }}
                   className="title"
                 >
                   {drink.strDrink}
                 </Card.Title>
                 <HeartFill
                   size={18}
                   style={{
                     width: "1rem",
                     height: "1rem",
                     marginTop: "10px",
                     position: "absolute",
                     marginLeft: "10px",
                     cursor: "pointer",
                     color: drink.isClicked ? "red" : "black",
                   }}
                   onClick={() => handleHeartClick(drink.idDrink)}
                 />
               </Card>
            ))}
        </div>
      </div>
    </div>
  )
}

export default Ordinary
