import React, { useEffect, useState } from "react";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const[glasses,setGlasses]=useState([]);
  const[ingredients,setIngredients]=useState([]);
  useEffect(() => {
    fetch("https://www.thecocktaildb.com/api/json/v1/1/list.php?c=list")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json(); 
      })
      .then((data) => {
        console.log(data.drinks);
        setCategories(data.drinks);
      })
      .catch((error) => {
        console.log(error.message);
      });
  }, []);

  useEffect(()=>{
    fetch("https://www.thecocktaildb.com/api/json/v1/1/list.php?g=list")
    .then((response)=>{
      return response.json(); 
      })
      .then((data)=>{
        console.log(data.drinks);
        setGlasses(data.drinks);
      })
      .catch((error)=>{
        console.log(error.message);
      });
  },[]);
  useEffect(()=>{
    fetch('https://www.thecocktaildb.com/api/json/v1/1/list.php?i=list')
    .then((response)=>{
      return response.json();
    })
    .then((data)=>{
      console.log(data.drinks);
      setIngredients(data.drinks);
    })
    .catch
  })

  return (
    <>
    <div style={{border:"1px solid black",margin:"20px",padding:"10px",borderRadius:"5px",backgroundColor:"pink"}}>
      <h3>Categories:</h3>
      <div style={{display:"flex",flexWrap:"wrap"}}>
      {categories.map((drink) => (
        <div key={drink.strCategory} >
          <h6 style={{border:"1px solid black",borderRadius:"5px",margin:"5px",width:"150px",height:"30px",textAlign:"center",padding:"1px"}}>{drink.strCategory}</h6>
        </div>
      ))}
    </div>
    </div>
    <div style={{border:"1px solid black",margin:"20px",padding:"10px",borderRadius:"5px",backgroundColor:"red"}}>
      <h3>Glasses:</h3>
      <div style={{display:"flex",flexWrap:"wrap"}}>
        {glasses.map((drink)=>(
          <div key={drink.strGlass}>
            <h6 style={{border:"1px solid black",borderRadius:"5px",margin:"5px",width:"180px",height:"50px",textAlign:"center",padding:"1px"}}>{drink.strGlass}</h6>
          </div>
        ))}
      </div>
    </div>
    </>
  );
};

export default Categories;
