import React, { useEffect, useState } from "react";
import './ingredient.css';
const Ingredients = () => {
  const [drinkIds] = useState([
    '11007','15346','17105','14029','178318','16943','12790','17224','16333','15567',
    '13086','15266','14364','17174','11243','14730','14956','11002','15330','178364','16942'
  ]);
  const [ingredients, setIngredients] = useState({});
  
  useEffect(() => {
    const fetchData = async () => {
      const requests = drinkIds.map(id =>
        fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`)
          .then(response => response.json())
      );
      try {
        const responses = await Promise.all(requests);
        const data = responses.reduce((acc, response, index) => {
          const drinkId = drinkIds[index];
          if (response.drinks) {
            acc[drinkId] = response.drinks[0];
          }
          return acc;
        }, {});
        setIngredients(data);
      } catch (error) {
        console.error("Error", error);
      }
    };
    fetchData();
  }, [drinkIds]);

  return (
    <div>
      <h1>Cocktail Information</h1>
      
        {Object.keys(ingredients).map(id => (
          <div>
          <ul key={id}>
            <li>{ingredients[id].strDrink}</li>
          </ul>
          <img src={ingredients[id].strDrinkThumb} ></img>
          <p>Category: {ingredients[id].strCategory}</p>
          <h4>Instructions</h4>
          <p>{ingredients[id].strInstructions}</p>
          <h4>Ingredients</h4>
          <h6>
              {ingredients[id].strIngredient1}:{ingredients[id].strMeasure1}
            </h6>
            <h6>
              {ingredients[id].strIngredient2}:{ingredients[id].strMeasure2}
            </h6>
            <h6>
              {ingredients[id].strIngredient3}:{ingredients[id].strMeasure3}
            </h6>
          </div>
        ))}
    </div>
  );
};

export default Ingredients;
