import React, { useEffect, useState } from "react";
import "./drink.css";
import { Card, Modal } from "react-bootstrap";
import { HeartFill } from "react-bootstrap-icons";

const Recipe = () => {
  const [category, setCategories] = useState([]);
  const [zoomedImage, setZoomedImage] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);

  useEffect(() => {
    fetch("https://www.thecocktaildb.com/api/json/v1/1/filter.php?c=Cocktail")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setCategories(data.drinks.map((drink) => ({ ...drink, isClicked: true })));
      })
      .catch((error) => {
        console.log(error.message);
      });
  }, []);

  const handleHeartClick = (drinkId) => {
    setCategories((prevCategories) => {
      return prevCategories.map((drink) => {
        if (drink.idDrink === drinkId) {
          return { ...drink, isClicked: !drink.isClicked };
        }
        return drink;
      });
    });
  };

  const handleItemClick = (drink) => {
    setSelectedItem(drink);
  };

  const handleCloseModal = () => {
    setSelectedItem(null);
  };

  return (
    <div className="main-wrapperr">
      <div className="drink-wrapper">
       
      </div>
      <h4
        style={{
          textAlign: "center",
          fontFamily: "initial",
          margin: "20px",
          marginTop: "40px",
          color: "rgb(201 200 197)",
        }}
      >
        MOST POPULAR
      </h4>
      <div className="wrapper">
        {category.map((drink) => (
          <Card
            key={drink.idDrink}
            style={{
              width: "16rem",
              margin: "20px",
              backgroundColor: "rgb(187 187 187)",
            }}
            onClick={() => handleItemClick(drink)}
          >
            <Card.Img
              className="image"
              src={drink.strDrinkThumb}
              style={{ width: "200px", height: "200px" }}
              alt={drink.strDrink}
            />
            <Card.Title
              style={{ fontSize: "16px", fontFamily: "Times New Roman" }}
              className="title"
            >
              {drink.strDrink}
            </Card.Title>
            <HeartFill
              size={18}
              style={{
                width: "1rem",
                height: "1rem",
                marginTop: "10px",
                position: "absolute",
                marginLeft: "10px",
                cursor: "pointer",
                color: drink.isClicked ? "red" : "black",
              }}
              onClick={() => handleHeartClick(drink.idDrink)}
            />
          </Card>
        ))}
      </div>
      <Modal show={selectedItem !== null} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedItem && selectedItem.strDrink}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedItem && (
            <>
              <img
                src={selectedItem.strDrinkThumb}
                alt={selectedItem.strDrink}
                style={{ width: "100%", marginBottom: "10px" }}
              />
              <p>{selectedItem.strInstructions}</p>
              {/* Add more details as needed */}
            </>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Recipe;
