import React, { useState, useEffect } from "react";
import "./drink.css";
import { Card } from "react-bootstrap";
import { HeartFill } from "react-bootstrap-icons";

const Recipe = () => {
  const [category, setCategories] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    fetch("https://www.thecocktaildb.com/api/json/v1/1/filter.php?c=Cocktail")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setCategories(data.drinks.map((drink) => ({ ...drink, isClicked: true })));
      })
      .catch((error) => {
        console.log(error.message);
      });
  }, []);

  const handleHeartClick = (drinkId) => {
    setCategories((prevCategories) => {
      return prevCategories.map((drink) => {
        if (drink.idDrink === drinkId) {
          return { ...drink, isClicked: !drink.isClicked };
        }
        return drink;
      });
    });
  };

  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  return (
    <div className="main-wrapper">
      <h1 className="heading">DRINKS</h1>
      <div className="images">
        {category.map((drink) => (
          <img
            key={drink.idDrink}
            src={drink.strDrinkThumb}
            alt={drink.strDrink}
            className="thumbnail-image"
            style={{ width: "200px", height: "200px" }}
            onClick={() => handleImageClick(drink.strDrinkThumb)}
          />
        ))}
      </div>
      {selectedImage && (
        <div className="enlarged-image-overlay" onClick={() => setSelectedImage(null)}>
          <img src={selectedImage} alt="enlarged" className="enlarged-image" />
        </div>
      )}
      <h4 className="subheading">MOST POPULAR</h4>
      <div className="wrapper">
        {category.map((drink) => (
          <Card
            key={drink.idDrink}
            style={{ width: "16rem", margin: "20px", backgroundColor: "rgb(187 187 187)" }}
          >
            <Card.Img
              className="image"
              src={drink.strDrinkThumb}
              style={{ width: "200px", height: "200px" }}
              alt={drink.strDrink}
            />
            <Card.Title
              style={{ fontSize: "16px", fontFamily: "Times New Roman" }}
              className="title"
            >
              {drink.strDrink}
            </Card.Title>
            <HeartFill
              size={18}
              style={{
                width: "1rem",
                height: "1rem",
                marginTop: "10px",
                position: "absolute",
                marginLeft: "10px",
                cursor: "pointer",
                color: drink.isClicked ? "red" : "black",
              }}
              onClick={() => handleHeartClick(drink.idDrink)}
            />
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Recipe;
