import React, { useEffect, useState } from "react";
import "./drink.css";
import { Card, Modal, Button } from "react-bootstrap";
import { Heart } from "react-bootstrap-icons";

const Recipe = () => {
  const [category, setCategories] = useState([]);
  const [selectedDrink, setSelectedDrink] = useState(null);
  const [drinkDetails, setDrinkDetails] = useState(null);

  useEffect(() => {
    if (selectedDrink) {
      fetchDrinkDetails(selectedDrink.idDrink);
    }
  }, [selectedDrink]);

  const fetchDrinkDetails = (drinkId) => {
    fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${drinkId}`)
      .then((response) => response.json())
      .then((data) => {
        setDrinkDetails(data.drinks[0]);
      })
      .catch((error) => {
        console.log(error.message);
      });
  };

  const handleCardClick = (drink) => {
    setSelectedDrink(drink);
  };

  const handleClose = () => {
    setSelectedDrink(null);
    setDrinkDetails(null);
  };

  return (
    <div className="main-wrapper">
      <div className="drink-wrapper">
        <h1 style={{ textAlign: "center", fontFamily: "initial", color: "rgb(201 200 197)" }}>DRINKS</h1>
      </div>

      <h4 style={{ textAlign: "center", fontFamily: "initial", margin: "20px", marginTop: "40px", color: "rgb(201 200 197)" }}>MOST POPULAR</h4>
      <div className="wrapper">
        {category.map((drink) => (
          <Card key={drink.idDrink} style={{ width: "16rem", margin: "20px", backgroundColor: "rgb(187 187 187)" }} onClick={() => handleCardClick(drink)}>
            <Heart size={18} style={{ width: "2rem", height: "1.3rem", marginTop: "10px", position: "absolute", marginLeft: "5px" }} />
            <Card.Img
              className="image"
              src={drink.strDrinkThumb}
              style={{ width: "200px", height: "200px" }}
              alt={drink.strDrink}
            />
            <Card.Title className="title">{drink.strDrink}</Card.Title>
          </Card>
        ))}
      </div>

      {/* Modal for showing details */}
      <Modal show={selectedDrink !== null} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedDrink?.strDrink}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <img src={selectedDrink?.strDrinkThumb} alt={selectedDrink?.strDrink} style={{ width: "100%" }} />
          <p>{selectedDrink?.strInstructions}</p>
          {/* Display additional details from drinkDetails if available */}
          {drinkDetails && (
            <>
              <p>Category: {drinkDetails.strCategory}</p>
              <p>Glass: {drinkDetails.strGlass}</p>
              {/* Add more details as needed */}
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>Close</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Recipe;
