import React, { useEffect, useState } from "react";
import "./drink.css";
import { Card } from "react-bootstrap";
import { HeartFill } from "react-bootstrap-icons";

const Recipe = () => {
  const [category, setCategories] = useState([]);

  useEffect(() => {
    fetch("https://www.thecocktaildb.com/api/json/v1/1/filter.php?c=Cocktail")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setCategories(
          data.drinks.map((drink) => ({ ...drink, isClicked: true }))
        );
      })
      .catch((error) => {
        console.log(error.message);
      });
  }, []);

  // const handleHeartClick = (drinkId) => {
  //   setCategories((prevCategories) => {
  //     return prevCategories.map((drink) => {
  //       if (drink.idDrink === drinkId) {
  //         return { ...drink, isClicked: !drink.isClicked };
  //       }
  //       return drink;
  //     });
  //   });
  // };

  return (
    <div className="main-wrapperr">
      <div className="drink-wrapper">
        <h1
          style={{
            textAlign: "center",
            fontFamily: "initial",
            color: "rgb(201 200 197)",
          }}
        >
          DRINKS
        </h1>
      </div>
      <h4
        style={{
          textAlign: "center",
          fontFamily: "initial",
          margin: "20px",
          marginTop: "40px",
          color: "rgb(201 200 197)",
        }}
      >
        MOST POPULAR
      </h4>
      <div className="wrapper">
        {category.map((drink) => (
          <Card
            key={drink.idDrink}
            style={{
              width: "16rem",
              margin: "20px",
              backgroundColor: "rgb(187 187 187)",
            }}
          >
            <Card.Img
              className="image"
              src={drink.strDrinkThumb}
              style={{ width: "200px", height: "200px" }}
              alt={drink.strDrink}
            />
            <Card.Title className="title">{drink.strDrink}
              </Card.Title>
            <HeartFill
              size={18}
              style={{
                width: "1rem",
                height: "1rem",
                marginTop: "10px",
                position: "absolute",
                marginLeft: "10px",
                cursor: "pointer",
                color: drink.isClicked ? "red" : "black",
              }} 
               
               onClick={() => handleHeartClick(drink.idDrink)}
              />
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Recipe;
