import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import { ArrowLeft } from 'react-bootstrap-icons';

const DrinkDetails = () => {
  const[details,setDetails]=useState([]);
  const{idDrink}=useParams();
  const navigate=useNavigate();
  useEffect(()=>{
    fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?${idDrink}`)
    .then((response)=>response.json())
    .then((data)=>{
      console.log(data);
      setDetails(data?.drinks?.[0]);
    })
  })

  const goBack=()=>{
    navigate(-1)
  }
  return (
    <>
    <div className='detail-wrapper'>
      <div className='backIcon'>
        <ArrowLeft onClick={goBack}/>
      </div>
      <div>
        <h1>{details.strDrink}</h1>
        <img src={details.strDrinkThumb} alt='err'></img>
        <p>Category:{details.strCategory}</p>
        <p>Alcoholic:{details.strAlcoholic}</p>
        <p>Glass:{details.strGlass}</p>
        <h5>Instructions</h5>
        <p>{details.strInstructions}</p>
        <h5>Ingredients</h5>
        <ul>
                <li style={{ marginLeft: "30px" }}>
                  {details.strIngredient1}:{details.strMeasure1}
                </li>
                <li style={{ marginLeft: "30px" }}>
                  {details.strIngredient2}:{details.strMeasure2}
                </li>
                <li style={{ marginLeft: "30px" }}>
                  {details.strIngredient3}:{details.strMeasure3}
                </li>
              </ul>
      </div>
    </div>
    </>
  )
}

export default DrinkDetails
