import React, { useEffect, useState } from "react";

const Ingredients = () => {
  const drinkIds=useState(['11007','15346','17105','14029','178318','16943','12790','17224','16333','15567','13086','15266','14364','17174','11243','14730','14956','11002','15330','178364','16942']);
  const [ingredient, setIngredient] = useState({});
  useEffect(() => {
    const fetchData=async()=>{
      const requests =drinkIds.map(id=>
        fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`)
        .then((response)=>response.json()) 
      );
      try {
        const responses = await Promise.all(requests);
        const data = responses.reduce((acc, response, index) => {
            const drinkIds = drinkIds[index];
            if (response.drinks) {
                acc[drinkIds] = response.drinks[0];
            }
            return acc;
        }, {});
        setIngredient(data);
      }catch(error){
        console.log("Error",error);
      }
    };
    fetchData();
    },[drinkIds])
    
      
  return (
    <>
    <div>
            <h1>Cocktail Information</h1>
            <ul>
                {Object.keys(ingredient).map(id => (
                    <li key={id}>
                        <strong>{ingredient[id].strDrink}</strong> - {ingredient[id].strCategory}
                    </li>
                ))}
            </ul>
        </div>
      {/* <div className="ingredient-wrapper">
        <h2>Ingredients</h2>
        {ingredient.map((drinks) => (
          <div>
            <h4 className="title">{drinks.strDrink}</h4>
            <p className="category">{drinks.strCategory}</p>
            <p>{drinks.strInstructions}</p>
            <img src={drinks.strDrinkThumb} alt="err"></img>
            <h6>
              {drinks.strIngredient1}:{drinks.strMeasure1}
            </h6>
            <h6>
              {drinks.strIngredient2}:{drinks.strMeasure2}
            </h6>
            <h6>
              {drinks.strIngredient3}:{drinks.strMeasure3}
            </h6>
          </div>
        ))}
      </div> */}
    </>
  );
};

export default Ingredients;
