import React from "react";
import "./home.css";
const Home = () => {
  return (
    <div className="body">
      <div className="main">
        <img
          src="https://www.foodrepublic.com/img/gallery/18-unusual-craft-cocktail-ingredients-you-should-try-at-least-once/l-intro-1689108572.jpg"
          alt="err"
          style={{
            width: "94vw",
            height: "100vh",
          }}
        ></img>
        <h1 style={{position:"absolute",color:"white",marginLeft:"450px",fontFamily:"Times New Roman"}}>COCKTAIL PARTY</h1>
      </div>
      <div style={{ display: "flex" }}>
       
          <div style={{ width: "280px" }}>
            <h2 style={{ marginLeft: "1px",color:"#d3cccc",textAlign:"center" }}>About Us</h2>
            <p>
              Every cocktail typically starts with a base spirit, which forms
              the primary alcoholic component of the drink. Common base spirits
              include vodka, rum, gin, tequila, whiskey, and brandy.Farming practices for cocktail ingredients can vary significantly
              depending on the specific ingredient and its agricultural
              requirements.The process of cocktail making, also known as mixology, involves
              several key steps to craft a well-balanced and flavorful drink.
            </p>
          </div>
        </div>
      </div>
  );
};

export default Home;
